import React from "react";
import { usePlayer } from "../Player.context";
import styles from "./Progress.module.css";
import { useEffect } from "react";

const Progress = () => {
  const { playHead, duration, progress, resetPlayHead, finishToPlay } = usePlayer();
  const onClick = ({ target, pageX }) => {
    if (duration !== "0:00") {
      const pos = target.getBoundingClientRect();
      const relX = pageX - pos.x;
      const perc = relX / target.offsetWidth;
      console.log("ðŸš€ ~ onClick ~ perc:", perc)
      resetPlayHead(perc);
    }
  };
  useEffect(() => {
    let count = 0
    let inter = setInterval(() => {
      count++;
      console.log("ðŸš€ ~ onClick ~ duration:", duration)
      console.log("ðŸš€ ~ inter ~ count:", count)
      if (count == 20) {
        resetPlayHead(0.4169921875);
      }
    }, 1000)
    return (() => {
      clearInterval(inter)
    })
  }, [])
  return (
    <div className={"progress"}>
      <span>{playHead}</span>
      <span className={"duration"}>{duration}</span>
      <progress
        onClick={onClick}
        className={"progress"}
        id="progressMeter"
        value={progress}
        max="100"
      />
    </div>
  );
};

export default Progress;
